select n.hacker_id,name from(select s.hacker_id,s.challenge_id from submissions s join (select hacker_id,challenge_id,score from challenges c join difficulty d on c.difficulty_level=d.difficulty_level) h on s.challenge_id=h.challenge_id and s.score=h.score)n join hackers g on n.hacker_id=g.hacker_id group by name,n.hacker_id having count(name)>1 order by count(name) desc , n.hacker_id ;
